import TinderCard from 'react-tinder-card'
import {useState} from 'react'
import { useCookies } from 'react-cookie'
import axios from 'axios'
import ChatContainer from '../components/ChatContainer'
const Dashboard = () => {

    const db = [
        {
            name: 'Richard Hendricks',
            url: 'https://i.imgur.com/Q9WPlWA.jpeg'
        },
        {
            name: 'Erlich Bachman',
            url: 'https://i.imgur.com/wDmRJPc.jpeg'
        },
        {
            name: 'Monica Hall',
            url: 'https://i.imgur.com/oPj4A8u.jpeg'
        },
        {
            name: 'Jared Dunn',
            url: 'https://i.imgur.com/dmwjVjG.jpeg'
        },
        {
            name: 'Dinesh Chugtai',
            url: 'https://i.imgur.com/MWAcQRM.jpeg'
        }
    ]

    const characters = db
    const [user, setUser] = useState(null)
    const [genderedUsers, setGenderedUsers] = useState(null)
    const [lastDirection, setLastDirection] = useState()
    const [cookies, setCookie, removeCookie] = useCookies(['user'])

    const userId = cookies.UserId

    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: {userId}
            })
            setUser(response.data)
        } catch (error) {
            console.log(error)
        }
    }
    const getGenderedUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8000/gendered-users', {
                params: {gender: user?.gender_interest}
            })
            setGenderedUsers(response.data)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getUser()
    },[])

    useEffect(() => {
        if(user) {
            getGenderedUsers()
        }
    },[user])

    const updateMatches = async (matchedUserId) => {
        try {
            await axios.put('http://localhost:8000/addmatch', {
                userId,
                matchedUserId
            })
            getUser()
        } catch (err) {
            console.log(err)
        }
    }


    const swiped = (direction, swipedUserId) => {
        if (direction === 'right') {
            updateMatches(swipedUserId)
        }
        setLastDirection(direction)
    }

    const outOfFrame = (name) => {
        console.log(name + ' left the screen!')
    }

    const matchedUserId = user?.matches.map(({uer_id}) => user_id).concat(userId)

    const filteredGenderedUsers = genderedUsers?.filter(genderedUser => !matchedUserIds.includes(genderedUser.user_id))

    return (
        <>

        </>

    )
}

export default Dashboard;
